import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import MovieList from './components/MovieList';
import MovieListHeading from './components/MovieListHeading';
import AddFavourites from './components/AddFavourites';
import RemoveFavourites from './components/RemoveFavourites';

const App = () => {
	const [movies, setMovies] = useState([
		{
			Title:'Godzilla',
			Year:'2014',
			Type:'movie',
			Poster:'https://cdn.onebauer.media/one/media/605b/69a9/dbf2/7f24/48f5/9560/godzilla-vs-kong.jpg?format=jpg&quality=80&width=440&ratio=16-9&resize=aspectfill'
		},
		{
			Title:'Conjiring',
			Year:'2014',
			Type:'movie',
			Poster:'data:image/jpeg;base64,'
		},
		{
			Title:'London has fallen',
			Year:'2014',
			Type:'movie',
			Poster:'https://i.pinimg.com/236x/d8/3b/53/d83b5353d21d94c0ab7a3427f20d6a0a.jpg'
		},
		{
			Title:'Harry Potter',
			Year:'2014',
			Type:'movie',
			Poster:'https://assets.www.warnerbros.com/drupal-root/public/DVD6.jpg'
		},
		{
			Title:'Iron Man',
			Year:'2014',
			Type:'movie',
			Poster:'https://m.media-amazon.com/images/M/MV5BMTczNTI2ODUwOF5BMl5BanBnXkFtZTcwMTU0NTIzMw@@._V1_FMjpg_UX1000_.jpg'
		},
		{
			Title:'Batman',
			Year:'2014',
			Type:'movie',
			Poster:'https://upload.wikimedia.org/wikipedia/en/8/8a/Dark_Knight.jpg'
		},
	]);
	const [favourites, setFavourites] = useState([]);
	const [searchValue, setSearchValue] = useState('');





	useEffect(() => {
		const movieFavourites = JSON.parse(
			localStorage.getItem('react-movie-app-favourites')
		);

		if (movieFavourites) {
			setFavourites(movieFavourites);
		}
	}, []);

	const saveToLocalStorage = (items) => {
		localStorage.setItem('react-movie-app-favourites', JSON.stringify(items));
	};

	const addFavouriteMovie = (movie) => {
		const newFavouriteList = [...favourites, movie];
		setFavourites(newFavouriteList);
		saveToLocalStorage(newFavouriteList);
	};

	const removeFavouriteMovie = (movie) => {
		const newFavouriteList = favourites.filter(
			(favourite) => favourite.imdbID !== movie.imdbID
		);

		setFavourites(newFavouriteList);
		saveToLocalStorage(newFavouriteList);
	};

	return (
		<div className='container-fluid movie-app'>
			<div className='row d-flex align-items-center mt-4 mb-4'>
				<MovieListHeading heading='Movies' />
			</div>
			<div className='row'>
				<MovieList
					movies={movies}
					handleFavouritesClick={addFavouriteMovie}
					favouriteComponent={AddFavourites}
				/>
			</div>
			<div className='row d-flex align-items-center mt-4 mb-4'>
				<MovieListHeading heading='Favourites' />
			</div>
			<div className='row'>
				<MovieList
					movies={favourites}
					handleFavouritesClick={removeFavouriteMovie}
					favouriteComponent={RemoveFavourites}
				/>
			</div>
		</div>
	);
};

export default App;
